import React, { Component } from 'react';
import './InteractiveWeather.css';

import { render } from 'react-dom'
import { Map, Marker, Popup, TileLayer } from 'react-leaflet'


class InteractiveWeather extends Component {

    constructor(props) {
        super(props);
        this.state = {
            render: true
        }
    }
    componentDidMount() {
        this.interval = setInterval(() => {
          this.setState({
            render: !this.state.render,
          })
        }, 5000)
      }
    
    componentWillUnmount() {
        clearInterval(this.interval)
      }
    
    render() {
        const position = [50.802463, -0.439711]

        return (
            <Map bounds={outer}>
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {this.state.render ? (
              <Pane name="cyan-rectangle" style={{ zIndex: 500 }}>
                <Rectangle bounds={outer} color="cyan" />
              </Pane>
            ) : null}
            <Pane name="yellow-rectangle" style={{ zIndex: 499 }}>
              <Rectangle bounds={inner} color="yellow" />
              <Pane name="purple-rectangle" className="purplePane-purplePane">
                <Rectangle bounds={outer} color="purple" />
              </Pane>
            </Pane>
          </Map>
        )
    }   
}

export default InteractiveWeather;
