import React, { Component, useContext } from 'react';
import './InteractiveWeather.css';

import { render } from 'react-dom'
import { Map, TileLayer, Marker, Popup} from 'react-leaflet';
import Control from 'react-leaflet-control';

import  WindContext from '../../context/wind-context';

class InteractiveWeather extends Component {

    constructor(props) {
        super(props);
        this.state = {
            mapCenter: [50.802897, -0.440456],
            zoomLevel: 12
        }
    }
    
    componentDidMount() {
        var color = React.useContext(WindContext.value);
    }

    render() {
        const stamenTonerTiles = 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png';
        const stamenTonerAttr = '&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors';

        return (
                <Map
                center={this.state.mapCenter}
                zoom={this.state.zoomLevel}
            >
                <TileLayer
                    attribution={stamenTonerAttr}
                    url={stamenTonerTiles}
                />

                {/* <Control position="topright">
                    <div class="map-control">
                        
                    </div>
                </Control> */}

                <Marker position={this.state.mapCenter}>
                    <Popup>A pretty CSS3 popup.<br />Easily customizable.</Popup>
                </Marker>
            </Map>
        )
    }   
}

export default InteractiveWeather;
