import { createGlobalStyle } from 'styled-components';

export const GlobalStyles = createGlobalStyle`
    body {
    margin: 0 !important;
    display: flex;
    height: 100%;
    
    background-color: ${({ theme }) => theme.backgroundColor};
    
    border-radius: 6px;
}


`;
