import React, { Component } from 'react';
import './InteractiveWeather.css';

import { render } from 'react-dom'
import { Map, TileLayer } from 'react-leaflet';

class InteractiveWeather extends Component {

    constructor(props) {
        super(props);
        this.state = {

        }
    }
    
    render() {
        const stamenTonerTiles = 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png';
        const stamenTonerAttr = '&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors';
        const mapCenter = [50.802897, -0.440456];
        const zoomLevel = 12;

        return (
            <Map
            center={mapCenter}
            zoom={zoomLevel}
        >
            <TileLayer
                attribution={stamenTonerAttr}
                url={stamenTonerTiles}
            />

<Control position="topright">
                <div
                    style={{
                        backgroundColor: 'black',
                        padding: '5px',
                    }}
                >
                    <div style={{ marginLeft: '37px' }}>
                        <button onClick={this.handleUpPanClick}>
                            Pan up
                        </button>
                    </div>
                    <div>
                        <button onClick={this.handleLeftPanClick}>
                            Pan left
                        </button>
                        <button onClick={this.handleRightPanClick}>
                            Pan right
                        </button>
                    </div>
                    <div style={{ marginLeft: '30px' }}>
                        <button onClick={this.handleDownPanClick}>
                            Pan down
                        </button>
                    </div>
                </div>
            </Control>

        </Map>

        )
    }   
}

export default InteractiveWeather;
