import React, { Component } from 'react';
import './HomeContent.css'
import WindCard from './WindCard';

class HomeContent extends Component {
    constructor(props){
        super(props);
        this.state = {
            wind: [],
        }
    }

    componentDidMount() {
        var data = {1: {name: "LOCAL", location: "Goring", distance: "0", speed: "40", direction: "WSW"}, 2: {name: "LANCING", location: "Lancing", distance: "6.6", speed: "38", direction: "WSW"}};
        this.setState({wind: data});

        // fetch('http://jsonplaceholder.typicode.com/users')
        // .then(res => res.json())
        // .then((data) => {
        //   this.setState({ wind: data })
        // })
        // .catch(console.log)
      }

    render(){
        return (
                <main id="layout-container">
                    <div id="content">
                        <div className="grid-container content-container">
                            <div className="grid-card">
                                <div className="full-size">
                                    <span className="card-title">INTERACTIVE WEATHER</span>
                                    <div className="colour-card blue full-size">
                                        
                                    </div>
                                </div>
                            </div>
                            <div className="grid-card">
                                <div className="full-size">
                                    <span className="card-title">WIND</span>
                                    <div>{this.state.wind.name}</div>
                                    <div className="full-size">
                                      <WindCard StationName={"LOCAL"} StationLocation={"Goring"} StationDistance={"0"} CurrentSpeed={"40"} Direction={"WSW"}/>
                                      <WindCard StationName={"WORTHING SAILING CLUB"} StationLocation={"Goring"} StationDistance={"0.8"} CurrentSpeed={"40"} Direction={"WSW"}/>
                                      <WindCard StationName={"LANCING"} StationLocation={"Lancing"} StationDistance={"6.6"} CurrentSpeed={"40"} Direction={"WSW"}/>
                                    </div>
                                </div>
                            </div>
                            <div className="grid-card">
                               <div className="full-size">
                                    <span className="card-title">TIDE</span>
                                    <div className="colour-card orange full-size">
                                        
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </main>
        )
    }
}

export default HomeContent;
